import React from 'react';
import logo from './logo.svg';
import './App.css';
import Search from './search';
import Add from './add';

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      listUser: []
    }
  }
  addUser=(user)=> {
    var list = this.state.listUser;
    list.push(user);
    this.setState({ listUser: list })
  }
  render() {
    return (
      <div className="App">
        <Search list={this.listUser}></Search>
        <Add add={this.addUser}></Add>
      </div>
    )
  }
}

export default App;
