import React from 'react'
class Search extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            listUser: [],
            matchUser: [],
            searchUser: ''
        }
        this.search = this.search.bind(this);
        this.FieldChange = this.FieldChange.bind(this);
    }
    search() {
        var listUser = this.state.listUser;
        var matched = this.state.matchUser;
        if (listUser.length == 0) {
            alert("no user in list");
            return;
        }
        for (var i = 0; i <= listUser.length; i++) {
            var founded = listUser[i].indexOf(this.state.searchUser);
            if (founded >= 0) {
                matched.push(this.state.listUser[founded])
            }
        }
        this.setState({ matchUser: matched })
    }
    FieldChange(e) {
        console.log(e.target.value);
        this.setState({ searchUser: e.target.value });
    }
    render() {
        return (
            <div>
                <input type="text" name="search" onChange={(e) => this.FieldChange(e)}></input>
                <button onClick={this.search}>Search</button>
                <table>
                    <tbody>
                        <tr>
                            <th>User name</th>
                        </tr>
                        {this.state.matchUser.map((user) => 
                            <tr>
                                <td>{user}</td>
                            </tr>
                        )}
                    </tbody>
                </table>

            </div>
        )
    }
}
export default Search
